const router = express.Router();

const imgData = "data:image/png;base64,"

router.post("/create-checkout-session", async (req, res) => {

	// console.log(req.body);

	const taxRate = await stripe.taxRates.create({
		display_name: 'BTW',
		inclusive: true,
		percentage: 21,
		country: 'NL',
	});

  const customer = await stripe.customers.create({
    metadata: {
      userId: req.body.userId ? req.body.userId : "NOACCOUNT",
    },
  });

  const line_items = req.body.cartItems.map((item) => {
    return {
      price_data: {
        currency: "eur",
        product_data: {
          name: item.name,
          images: [item.image.url],
          metadata: {
            id: item.id,
          },
        },
        unit_amount: item.price * 100,
      },
      quantity: item.cartQuantity,
    };
  });

	let totalProducts = 0;

	for (const item of req.body.cartItems) {
		if (item.hasOwnProperty('cartQuantity')) {
			totalProducts += item.cartQuantity;
		}
	}

	let discount = 0;

	if (totalProducts === 2) {
		discount = 5; // Apply 5% discount if they buy 2 products
	} else if (totalProducts >= 3) {
		discount = 10; // Apply 10% discount if they buy 3 or more products
	}

	console.log(discount);

  // "sofort", "bancontact", "klarna", "customer_balance", "sepa_debit", "giropay", "eps",
  const session = await stripe.checkout.sessions.create({
    payment_method_types: ["bancontact", "ideal", "paypal", "klarna", "card"],
    shipping_address_collection: {
      allowed_countries: ["NL","DE", "BE"],
    },
    shipping_options: [
      {
        shipping_rate_data: {
          type: "fixed_amount",
          fixed_amount: {
            amount: req.body.insurance ? 299 : 0,
            currency: "eur",
          },
          display_name: req.body.insurance ? "Verzekerd Verzenden" : "Gratis Verzendkosten",
          delivery_estimate: {
            minimum: {
              unit: "business_day",
              value: 4,
            },
            maximum: {
              unit: "business_day",
              value: 7,
            },
          },
        }}],
    line_items,
		discounts: discount !== 0 ? [
			{
				coupon: discount === 5 ? "rkp7ttoT" : discount === 10 ? "uf3fkBJq" : ""
				// 10%: uf3fkBJq, 5%: rkp7ttoT
			}
		] : [],
    mode: "payment",
    customer: customer.id,
    locale: "nl",
    success_url: `${process.env.CLIENT_URL}/betaald`,
    cancel_url: `${process.env.CLIENT_URL}/winkelwagen`,
  });

  // res.redirect(303, session.url);
  res.send({ url: session.url });
});

// Create order function

const createOrder = async (customer, data, line_items) => {
  const newOrder = await new Order({
    userId: await customer?.metadata?.userId,
    customerId: await data?.customer,
    paymentIntentId: await data?.payment_intent,
    products: await line_items?.data,
    subtotal: await data?.amount_subtotal,
    total: await data?.amount_total,
    shipping: await data?.customer_details,
    payment_method: "stripe",
    payment_status: await data?.payment_status,
  });

  try {
    const savedOrder = await newOrder.save();
    // await sendPaymentReceive(customer.email, "Your payment has been received", customer.id)
    // console.log("Processed Order:", savedOrder);
  } catch (err) {
    console.log(err);
  }
};

